              <img src={item?.image} alt={item?.image} className="w-[100%]" />
              <div>
                <h4>{item?.title}</h4>
                <p>{item?.descr}</p>
              </div>

              <div className="flex item-center justify-between">
                <Button
                  type="primary"
                  onClick={() => {
                    navigate(`/update/${item?._id}`);
                  }}
                >
                  Update
                </Button>
                <Button
                  className="bg-red-500 text-white"
                  onClick={() => {
                    deleteTravel(item?._id);
                  }}
                >
                  Delate
                </Button>
              </div>
            </div>
          </Col>
        ))}
      </Row>
    </>
  );
};

export default ListPage;
