import { useQuery } from "@tanstack/react-query";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Card } from "antd";

const ListPage = () => {
  const { name } = useParams();

  const { data, isLoading, error } = useQuery({
    queryKey: ["board", name],
    queryFn: async () => {
      if (!name) return null; // name bo'lmasa query ishlamasin
      const res = await axios.get(`http://localhost:3000/api/boards`);
      // name bo‘yicha filter yoki find

      return res.data.find((board) => board.name === name);
    },
    enabled: !!name, // faqat name mavjud bo'lsa fetch qiladi
  });

  const { data: dataList } = useQuery({
    queryKey: ["list-one"],
    queryFn: async () => {
      if (!name) return null;
      await axios.get(`http://localhost:3000/api/lists?boardId=${data?.id}`);
    },
    enabled: !!name, // faqat name mavjud bo'lsa fetch qiladi
  });

  console.log(dataList);

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;
  if (!data) return <div>No data found</div>;

  return (
    <>
      <Card></Card>
    </>
  );
};

export default ListPage;
