import { useQuery } from "@tanstack/react-query";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Card } from "antd";

const ListPage = () => {
  const { name } = useParams();

  // 1. Boardni name orqali topib, faqat _id ni qaytarish
  const {
    data: boardId,
    isLoading: boardLoading,
    error: boardError,
  } = useQuery({
    queryKey: ["boardId", name],
    queryFn: async () => {
      const res = await axios.get(`http://localhost:3000/api/boards`);
      const board = res.data.find((b) => b.name === name);
      if (!board) throw new Error("Board topilmadi");
      return board._id; // faqat _id qaytadi
    },
    enabled: !!name,
  });

  // 2. Board ID bo‘yicha listlarni olish
  const {
    data: lists,
    isLoading: listsLoading,
    error: listsError,
  } = useQuery({
    queryKey: ["lists", boardId],
    queryFn: async () => {
      const res = await axios.get(
        `http://localhost:3000/api/lists?boardId=${boardId}`
      );
      return res.data;
    },
    enabled: !!boardId, // faqat boardId mavjud bo‘lsa ishlaydi
  });

  console.log(lists);

  if (boardLoading || listsLoading) return <p>Loading...</p>;
  if (boardError) return <p>{boardError.message}</p>;
  if (listsError) return <p>{listsError.message}</p>;

  return (
    <>
      {lists?.data?.map((list) => (
        <Card key={list._id} title={list.name}>
          <p>{list.description}</p>
        </Card>
      ))}
      <Card>
        <h1> Board</h1>
      </Card>
    </>
  );
};

export default ListPage;
