import { useQuery } from "@tanstack/react-query";
import axios from "axios";

// API chaqirish funksiyasi
const fetchListsByBoardName = async (boardName) => {
  const res = await axios.get(
    `http://localhost:5000/api/lists?boardName=${encodeURIComponent(boardName)}`
  );
  return res.data;
};

// Hook shaklida ishlatish
export const useListsByBoardName = (boardName) => {
  return useQuery({
    queryKey: ["lists", boardName], // har xil boardName uchun kechlash alohida bo‘ladi
    queryFn: () => fetchListsByBoardName(boardName),
    enabled: !!boardName, // boardName bo‘lmasa request qilinmaydi
  });
};
