import React, { useState } from "react";
import { Layout, Menu, Card, Row, Col } from "antd";

const { Header, Sider, Content } = Layout;

const MainComp = () => {
  const [selectedBoard, setSelectedBoard] = useState("boardsData[0]");

  const boardsData = [
    {
      id: 1,
      name: "Frontend Project",
      lists: [
        {
          id: 1,
          name: "Todo",
          tasks: ["Setup project", "Install dependencies"],
        },
        { id: 2, name: "In Progress", tasks: ["Build UI", "Integrate API"] },
        { id: 3, name: "Done", tasks: ["Create repo", "Initial commit"] },
      ],
    },
    {
      id: 2,
      name: "Backend Project",
      lists: [
        { id: 1, name: "Todo", tasks: ["Setup database", "API design"] },
        { id: 2, name: "In Progress", tasks: ["User auth", "Payment system"] },
        { id: 3, name: "Done", tasks: ["Initial setup"] },
      ],
    },
  ];
  return (
    <>
      <Layout style={{ minHeight: "100vh" }}>
        {/* Sidebar */}
        <Sider>
          <Menu
            theme="dark"
            mode="inline"
            // selectedKeys={[String(selectedBoard.id)]}
            onClick={({ key }) => {
              const board = boardsData.find((b) => b.id === Number(key));
              setSelectedBoard(board);
            }}
          >
            {boardsData.map((board) => (
              <Menu.Item key={board.id}>{board.name}</Menu.Item>
            ))}
          </Menu>
        </Sider>

        {/* Main Content */}
        <Layout>
          <Header style={{ background: "#fff", padding: "0 16px" }}>
            {/* <h2>{selectedBoard.name}</h2> */}
          </Header>
          <Content style={{ margin: "16px" }}>
            <Row gutter={16}>
              {/* {boardsData.map((list) => (
                <Col key={list.id} span={8}>
                  <Card title={list.name} bordered>
                    {list.tasks.map((task, index) => (
                      <Card
                        key={index}
                        style={{
                          marginBottom: "8px",
                          background: "#f5f5f5",
                        }}
                      >
                        {task}
                      </Card>
                    ))}
                  </Card>
                </Col>
              ))} */}
            </Row>
          </Content>
        </Layout>
      </Layout>
    </>
  );
};

export default MainComp;
